    )
BEGIN

  DECLARE done INT DEFAULT 0;

  DECLARE end_timez TIME;

  DECLARE percentagez DECIMAL(15,3);

  DECLARE user_idz VARCHAR(20);

  DECLARE id_logz BIGINT(20);

  DECLARE cursor_data CURSOR FOR SELECT end_time, percentage, user_id, id_log FROM cfg_init_room_rate_last_deal WHERE room_rate_code = room_rate_codex AND start_time < start_timex AND end_time > end_timex;

  DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done = 1;



  OPEN cursor_data;



  FETCH cursor_data INTO end_timez, percentagez, user_idz, id_logz;

  IF NOT done THEN

    UPDATE cfg_init_room_rate_last_deal SET

      end_time = SUBTIME(from_timex, "01:00:00")

    WHERE id_log = id_logz;

    INSERT INTO cfg_init_room_rate_session VALUES(room_rate_codex, ADDTIME(end_timex, "01:00:00"), end_timez, percentagez, user_idz, NULL);

  END IF;



  CLOSE cursor_data;



  UPDATE cfg_init_room_rate_last_deal SET

    start_time = ADDTIME(end_timex, "01:00:00")

  WHERE room_rate_code = room_rate_codex AND start_time <= end_timex AND end_time > end_timex;



  UPDATE cfg_init_room_rate_last_deal SET

    end_time = SUBTIME(start_timex, "01:00:00")

