    )
BEGIN

  DECLARE done INT DEFAULT 0;

  DECLARE is_pair_with_foliox ENUM("-1","0");

  DECLARE voidx ENUM("-1","0");

  DECLARE transfer_pair_idx BIGINT(20);

  DECLARE cursor_transfer_pair CURSOR FOR SELECT is_pair_with_folio, transfer_pair_id, void FROM guest_deposit WHERE id_log = id_logx;

  DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done = 1;

  SET max_sp_recursion_depth = 255;



  IF id_logx <> 0 THEN



    OPEN cursor_transfer_pair;



    REPEAT

      FETCH cursor_transfer_pair INTO is_pair_with_foliox, transfer_pair_idx, voidx;

      IF NOT done THEN

        IF is_pair_with_foliox = "0" AND voidx = "0" THEN

		  UPDATE guest_deposit SET

      		void = "-1",

      		void_date = NOW(),

      		void_by = void_by,

      		void_reason = void_reason,

      		user_id = user_id

          WHERE id_log = id_logx;



          UPDATE acc_foreign_cash SET user_id = user_id WHERE id_transaction = id_logx AND id_table = 1;

  		  DELETE FROM acc_foreign_cash WHERE id_transaction = id_logx AND id_table = 1;



          CALL update_guest_deposit_void(transfer_pair_idx, void_by, void_reason, user_id);

        ELSE

          CALL update_sub_folio_void(transfer_pair_idx, void_by, void_reason, user_id);

        END IF;

      END IF;

    UNTIL done END REPEAT;

    CLOSE cursor_transfer_pair;



    UPDATE guest_deposit SET

      void = "-1",

      void_date = NOW(),

      void_by = void_by,

      void_reason = void_reason,

      user_id = user_id

    WHERE id_log = id_logx;

    CALL delete_acc_foreign_cash_by_transaction(id_logx, 1, user_id);

  END IF;

