CREATE DEFINER=`root`@`localhost` PROCEDURE `update_reservation_departure`(
        IN numberx BIGINT(20),
        IN departure DATETIME,
        IN user_id VARCHAR(20)
    )
BEGIN
  DECLARE done INT DEFAULT 0;
  DECLARE guest_detail_idx BIGINT(20);
  DECLARE cursor_guest_detail_id CURSOR FOR SELECT guest_detail_id FROM reservation WHERE number = numberx;
  DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET done = 1;

  OPEN cursor_guest_detail_id;
  FETCH cursor_guest_detail_id INTO guest_detail_idx;

  IF NOT done THEN
    UPDATE guest_detail SET
      departure = departure,
      departure_unixx = UNIX_TIMESTAMP(DATE(departure)),
      departure_res = departure,
      user_id = user_id
    WHERE id = guest_detail_idx;
  END IF;

  CLOSE cursor_guest_detail_id;
END ;;
